package Day06;

public class _08_toLower_UpperCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String str1 = "bit";
		String str2 = "HELLO JAVA";
		
		//알파벳 대문자로 치환
		System.out.println(str1.toUpperCase());
		//알파벳 소문자로 치환
		System.out.println(str2.toLowerCase());
	}

}
