package Day06;

public class _13_ceil_floor_round {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double num1 = 1.4;
		double num2 = 2.6;
		
		System.out.println("num1의 올림 값은 : " + Math.ceil(num1));
		System.out.println("num2의 버림 값은 : " + Math.floor(num2));
		
		System.out.println("num1의 반올림 값은 : " + Math.round(num1));
		System.out.println("num1의 반올림 값은 : " + Math.round(num2));
		
	}

}
